/* eslint-disable react-hooks/exhaustive-deps */
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  IconButton,
  TextField,
} from "@mui/material";
import { useEffect, useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import Tabledata from "./components/Tabledata";

var id = 0;

interface TableData {
  id: number;
  categories: {
    id: number;
    category: string;
    values: { typeName: string; typeValue: number }[];
    subcategories: { categories: any }[];
    parentId: number;
    currentLevel: number;
  }[];
}

function App() {
  // ------------------------------Declarations-------------------------
  const [forceRender, setForceRender] = useState(false);
  const [theme, setTheme] = useState("light");
  const [open, setOpen] = useState(false);

  var body: any = document.querySelector("body");
  const [tabledata, setTableData] = useState<TableData[]>([]);
  // ------------------------------Declarations-------------------------

  // ------------------------------Functions-------------------------

  useEffect(() => {
    if (theme === "light") {
      body.style.backgroundColor = "#ffffff";
    } else {
      body.style.backgroundColor = "#000000";
    }
  }, [theme]);



  // ------------------------------Add New Table Function-------------------------
  function addTable(e: any) {
    e.preventDefault();

    id++;
    setTableData([
      ...tabledata,
      {
        id,
        categories: [],
      },
    ]);

    setOpen(false);
  }

  // ------------------------------Function to add more than one types when creating table.-------------------------

  // ------------------------------Functions End-------------------------

  // ------------------------------Component-------------------------
  return (
    <div className="App">
      <h1
        style={{
          fontFamily: "",
          textAlign: "center",
          textTransform: "uppercase",
          letterSpacing: "0.1em",
          color: theme === "light" ? "#000" : "#fff",
        }}
      >
        dynamic Tree View
      </h1>
      <Button
        variant={theme === "light" ? "outlined" : "contained"}
        color={theme === "light" ? "success" : "primary"}
        className="primary-create-btn"
        onClick={handleOpen}
      >
        Create Table
      </Button>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Create New Table</DialogTitle>
        <form onSubmit={(e) => addTable(e)} className="dialog-box">
          <DialogContent>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Table Name"
              type="text"
              fullWidth
              variant="outlined"
              onChange={(e) =>
                setFormData({ ...formdata, tabletitle: e.target.value })
              }
              required
            />
            <TextField
              margin="dense"
              id="category"
              label="Category Name"
              type="text"
              fullWidth
              variant="outlined"
              onChange={(e) =>
                setFormData({ ...formdata, category: e.target.value })
              }
              required
            />

            {formdata.type.map((item, index) => {
              return (
                <TextField
                  key={index}
                  margin="dense"
                  id={`type-${index}`}
                  label="Type Name"
                  type="text"
                  fullWidth
                  style={{ flexDirection: "column", display: "flex" }}
                  variant="outlined"
                  value={item.value}
                  onChange={(e) => {
                    const newType = [...formdata.type];
                    newType[index] = { value: e.target.value };
                    setFormData({ ...formdata, type: newType });
                  }}
                  required
                />
              );
            })}

            <IconButton
              aria-label="Add Another Type"
              color="primary"
              size="small"
              onClick={() => addType()}
              style={{
                position: "absolute",
                transform: "translate(435px,-45px)",
              }}
            >
              <AddIcon fontSize="inherit" />
            </IconButton>
          </DialogContent>
          <DialogActions>
            <Button variant="contained" color="error" onClick={handleClose}>
              Cancel
            </Button>

            <Button type="submit" variant="contained" color="success">
              Create
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      {tabledata.map((item: TableData) => (
        <Tabledata
          key={item.id}
          tabledata={item}
          theme={theme}
          setTableData={setTableData}
          maindata={tabledata}
          setForceRender={setForceRender}
          forceRender={forceRender}
        />
      ))}
    </div>
  );
}
// ------------------------------Component-------------------------

export default App;
