/* eslint-disable react-hooks/exhaustive-deps */
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  IconButton,
  TextField,
} from "@mui/material";
import { useEffect, useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import Tabledata from "./components/Tabledata";

var id = 0;

interface TableData {
  id: number;
  categories: {
    id: number;
    category: string;
    values: { typeName: string; typeValue: number }[];
    subcategories: { categories: any }[];
    parentId: number;
    currentLevel: number;
  }[];
}

function App() {
  // ------------------------------Declarations-------------------------
  const [forceRender, setForceRender] = useState(false);
  const [theme, setTheme] = useState("light");
  const [open, setOpen] = useState(false);

  var body: any = document.querySelector("body");
  const [tabledata, setTableData] = useState<TableData[]>([]);
  // ------------------------------Declarations-------------------------

  // ------------------------------Functions-------------------------

  useEffect(() => {
    if (theme === "light") {
      body.style.backgroundColor = "#ffffff";
    } else {
      body.style.backgroundColor = "#000000";
    }
  }, [theme]);

  // ------------------------------Add New Table Function-------------------------
  function addTable(e: any) {
    e.preventDefault();

    id++;
    setTableData([
      ...tabledata,
      {
        id,
        categories: [],
      },
    ]);

    setOpen(false);
  }

  // ------------------------------Function to add more than one types when creating table.-------------------------

  // ------------------------------Functions End-------------------------

  // ------------------------------Component-------------------------
  return (
    <div className="App">
      <h1
        style={{
          fontFamily: "",
          textAlign: "center",
          textTransform: "uppercase",
          letterSpacing: "0.1em",
          color: theme === "light" ? "#000" : "#fff",
        }}
      >
        dynamic Tree View
      </h1>
      <Button
        variant={theme === "light" ? "outlined" : "contained"}
        color={theme === "light" ? "success" : "primary"}
        className="primary-create-btn"
        onClick={() => addTable}
      >
        Create Table
      </Button>



      {tabledata.map((item: TableData) => (
        <Tabledata
          key={item.id}
          tabledata={item}
          theme={theme}
          setTableData={setTableData}
          maindata={tabledata}
          setForceRender={setForceRender}
          forceRender={forceRender}
        />
      ))}
    </div>
  );
}
// ------------------------------Component-------------------------

export default App;
